import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';

export const OrderContext = createContext();

const OrderProvider = ({ children }) => {
  const [orders, setOrders] = useState([]);
//   const [userId] = useState('6836ba6bb010a67cfc760bb1'); // bạn có thể lấy từ Auth
    const [userId, setUserId] = useState('');

useEffect(() => {
  const token = localStorage.getItem('accessToken');
  if (token) {
    try {
      const decoded = jwtDecode(token);
      setUserId(decoded.id); 
    } catch (err) {
      console.error('Invalid token:', err);
    }
  }
}, []);

useEffect(() => {
  if (userId) {
    fetchCart();
  }
}, [userId]); 
  
  // Fetch cart on mount
  
  const fetchCart = async () => {
    try {
      const res = await axios.get(`http://localhost:4000/cart/${userId}`);
      
      setOrders(res.data.items || []);
    } catch (err) {
      console.error('Fetch cart failed:', err);
    }
  };
  useEffect(() => {
        fetchCart();
    }, []);

  // Add to cart
  const handleCart = async (product) => {
    try {
      await axios.post(`http://localhost:4000/cart/add`, {
        userId,
        productId: product._id,
        quantity: 1,
      });
      fetchCart(); // Refresh cart
    } catch (err) {
      console.error('Add to cart failed:', err);
    }
  };

  // Remove one product
  const removeProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:4000/cart/remove`, {
        data: {
          userId,
          productId
        }
      });
      fetchCart(); // Refresh cart
    } catch (err) {
      console.error('Remove product failed:', err);
    }
  };

  // Clear entire cart
  const clearCart = async () => {
    try {
      const promises = orders.map(item => removeProduct(item.product._id));
      await Promise.all(promises);
      setOrders([]);
    } catch (err) {
      console.error('Clear cart failed:', err);
    }
  };

  const value = {
  orders,
  fetchCart, // thêm nếu bạn muốn gọi lại từ bên ngoài
  handleCart,
  removeProduct,
  clearCart,
};

  return (
    <OrderContext.Provider value={value}>
      {children}
    </OrderContext.Provider>
  );
};

export default OrderProvider;
