import React, { useState } from 'react';
import { AiFillStar, AiOutlineStar } from 'react-icons/ai';
import { MdDeleteOutline } from 'react-icons/md';
import Rating from 'react-rating';
import Bounce from 'react-reveal/Bounce';
import useOrder from '../../hooks/useOrder';

const OrderCard = ({ product, quantity, totalPrice, onPlaceOrder }) => {
  const { removeProduct, updateQuantity } = useOrder(); 
  const { name, images, price, _id, slug, rating = 4, reviews = 0 } = product;

  const [localQuantity, setLocalQuantity] = useState(quantity);

  // Thông tin khách hàng
  const [paymentMethod, setPaymentMethod] = useState('momo');
  const [shippingAddress, setShippingAddress] = useState({
    recipientName: 'Nguyễn Văn A',
    phone: '0912345678',
    address: '123 Trần Hưng Đạo, Quận 1, TP.HCM',
  });
  const [note, setNote] = useState('Giao trong giờ hành chính');

  const handleIncrease = () => setLocalQuantity(prev => prev + 1);
  const handleDecrease = () => setLocalQuantity(prev => (prev > 1 ? prev - 1 : 1));
  const handleUpdate = () => {
    updateQuantity(_id, localQuantity);
  };

  // Hàm xử lý đặt hàng (gọi callback từ cha)
  const handlePlaceOrder = () => {
    const orderData = {
      paymentMethod,
      shippingAddress,
      note,
      totalPrice,
      products: [{ productId: _id, quantity: localQuantity }],
    };
    onPlaceOrder(orderData);
  };

  // Handle input change cho địa chỉ
  const handleAddressChange = (field, value) => {
    setShippingAddress(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Bounce left>
      <div className="space-y-4 bg-gray-50 rounded-xl p-4 transition hover:scale-105 hover:shadow-xl duration-700">
        <div className="flex space-x-5">
          <div>
            <img className="w-40" src={images[0]} alt={name} />
          </div>
          <div className="flex flex-col justify-between flex-grow">
            <h1 className="text-lg poppins text-gray-700">{name}</h1>
            <h2 className="text-gray-900 font-bold poppins text-2xl">{price} VNĐ</h2>

            <div className="flex items-center space-x-2">
              <button onClick={handleDecrease} className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300">-</button>
              <span>{localQuantity}</span>
              <button onClick={handleIncrease} className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300">+</button>
              <button onClick={handleUpdate} className="ml-4 px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700">Cập nhật</button>
            </div>

            <div className="flex items-center space-x-2 mt-2">
              <Rating emptySymbol={<AiOutlineStar className="text-gray-600 text-xl" />} fullSymbol={<AiFillStar className="text-yellow-400 text-xl" />} initialRating={rating} readonly />
              <span className="text-gray-600">({reviews})</span>
            </div>
          </div>
          <div>
            <MdDeleteOutline className="text-2xl text-gray-600 cursor-pointer" onClick={() => removeProduct(_id)} />
          </div>
        </div>

        {/* Thông tin khách hàng */}
        <div className="bg-white p-4 rounded shadow-md space-y-3">
          <h3 className="font-semibold text-lg">Thông tin khách hàng</h3>

          <div>
            <label className="block font-medium">Phương thức thanh toán:</label>
            <select
              value={paymentMethod}
              onChange={e => setPaymentMethod(e.target.value)}
              className="border rounded px-2 py-1 w-full"
            >
              <option value="momo">Momo</option>
              <option value="bank_transfer">Chuyển khoản</option>
              <option value="cash_on_delivery">Thanh toán trực tiếp</option>
            </select>
          </div>

          <div>
            <label className="block font-medium">Tên người nhận:</label>
            <input
              type="text"
              value={shippingAddress.recipientName}
              onChange={e => handleAddressChange('recipientName', e.target.value)}
              className="border rounded px-2 py-1 w-full"
            />
          </div>

          <div>
            <label className="block font-medium">Số điện thoại:</label>
            <input
              type="text"
              value={shippingAddress.phone}
              onChange={e => handleAddressChange('phone', e.target.value)}
              className="border rounded px-2 py-1 w-full"
            />
          </div>

          <div>
            <label className="block font-medium">Địa chỉ:</label>
            <input
              type="text"
              value={shippingAddress.address}
              onChange={e => handleAddressChange('address', e.target.value)}
              className="border rounded px-2 py-1 w-full"
            />
          </div>

          <div>
            <label className="block font-medium">Ghi chú:</label>
            <textarea
              value={note}
              onChange={e => setNote(e.target.value)}
              className="border rounded px-2 py-1 w-full"
            />
          </div>

          {/* Tổng tiền */}
          <div className="text-right font-bold text-xl">
            Tổng tiền: {totalPrice} VNĐ
          </div>

          <button
            onClick={handlePlaceOrder}
            className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded font-semibold"
          >
            Đặt hàng
          </button>
        </div>
      </div>
    </Bounce>
  );
};

export default OrderCard;
