    import { useState, useEffect } from "react";
    import useFetch from "../../hooks/useFetch";
    import { jwtDecode } from "jwt-decode";

    const InfoForm = () => {
    const token = localStorage.getItem("accessToken");
    const decoded = jwtDecode(token);

    const data = useFetch(`auth/${decoded.id}`, {
        asArray: false,
        hasDataKey: true,
    });

    const [fullname, setFullname] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");
    const [avatarUrl, setAvatarUrl] = useState(""); // URL ảnh để hiển thị
    const [avatarFile, setAvatarFile] = useState(null); // File ảnh thật để upload

    useEffect(() => {
        if (data.data) {
        setFullname(data.data.fullname || "");
        setPhone(data.data.phone || "");
        setAddress(data.data.address || "");
        setAvatarUrl(
            data.data.avatar ||
            "https://cdn-icons-png.flaticon.com/512/236/236832.png"
        );
        }
    }, [data.data]);

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
        setAvatarFile(file); // lưu file để gửi sau này
        const imageUrl = URL.createObjectURL(file); // tạo URL tạm thời để hiển thị ảnh
        setAvatarUrl(imageUrl);
        }
    };
const handleSubmit = async (e) => {
  e.preventDefault();

  const formData = new FormData();
  formData.append("fullname", fullname);
  formData.append("phone", phone);
  formData.append("address", address);
  if (avatarFile) {
    formData.append("avatar", avatarFile); // chỉ gửi nếu có ảnh mới
  }

  try {
    const response = await fetch(`http://localhost:4000/auth/${decoded.id}`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    if (response.ok) {
      alert("Cập nhật thành công!");
      const result = await response.json();
      setAvatarUrl(result.avatar); // cập nhật lại URL nếu backend trả URL mới
    } else {
      const errorData = await response.json();
      alert("Cập nhật thất bại: " + errorData.message);
    }
  } catch (error) {
    alert("Lỗi khi cập nhật: " + error.message);
  }
};

    return (
        <>
        <h2 className="text-2xl font-semibold text-gray-700 mb-6">
            Cập nhật thông tin
        </h2>
        <form className="space-y-6" onSubmit={(e) => e.preventDefault()}>
            <div>
            <label className="block mb-1">Tên</label>
            <input
                type="text"
                className="w-full border px-4 py-2 rounded"
                placeholder="Nhập tên"
                value={fullname}
                onChange={(e) => setFullname(e.target.value)}
            />
            </div>
            <div>
            <label className="block mb-1">Số điện thoại</label>
            <input
                type="text"
                className="w-full border px-4 py-2 rounded"
                placeholder="Nhập số điện thoại"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
            />
            </div>
            <div>
            <label className="block mb-1">Địa chỉ</label>
            <input
                type="text"
                className="w-full border px-4 py-2 rounded"
                placeholder="Nhập địa chỉ"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
            />
            </div>
            <div>
            <label className="block mb-1">Ảnh đại diện</label>
            <img
                src={
                avatarUrl ||
                "https://cdn-icons-png.flaticon.com/512/236/236832.png"
                }
                alt="Avatar Preview"
                className="w-24 h-24 object-cover rounded-full mb-2"
            />
            <input
                type="file"
                accept="image/*"
                onChange={handleAvatarChange}
                className="w-full"
            />
            </div>
            <button className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            Cập nhật
            </button>
        </form>
        </>
    );
    };

    export default InfoForm;
