import { useState, useEffect } from "react";
import useFetch from "../../hooks/useFetch";
import {jwtDecode} from "jwt-decode";

const InfoForm = () => {
  const token = localStorage.getItem("accessToken");
  let decoded = null;
  try {
    decoded = token ? jwtDecode(token) : null;
  } catch (error) {
    console.error("Token decode error:", error);
  }

  // Nếu không có token hoặc decode lỗi thì không gọi API
  const data = useFetch(
    decoded ? `auth/${decoded.id}` : null,
    {
      asArray: false,
      hasDataKey: true,
    }
  );
  console.log(data.data);
  
  const [fullname, setFullname] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [avatarUrl, setAvatarUrl] = useState(
    "https://cdn-icons-png.flaticon.com/512/236/236832.png"
  ); // URL ảnh để hiển thị
  const [avatarFile, setAvatarFile] = useState(null); // File ảnh thật để upload

  useEffect(() => {
    if (data?.data) {
      setFullname(data.data.fullname || "");
      setPhone(data.data.phone || "");
      setAddress(data.data.address || "");
      setAvatarUrl(
        data.data.avatar ||
          "https://cdn-icons-png.flaticon.com/512/236/236832.png"
      );
    }
  }, [data?.data]);

  // Xử lý tạo URL object cho preview ảnh, đồng thời cleanup khi đổi ảnh
  useEffect(() => {
    return () => {
      if (avatarUrl && avatarUrl.startsWith("blob:")) {
        URL.revokeObjectURL(avatarUrl);
      }
    };
  }, [avatarUrl]);

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAvatarFile(file); // lưu file để gửi sau này
      const imageUrl = URL.createObjectURL(file); // tạo URL tạm thời để hiển thị ảnh
      setAvatarUrl(imageUrl);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!decoded) {
      alert("Token không hợp lệ hoặc hết hạn.");
      return;
    }

    const formData = new FormData();
    formData.append("fullname", fullname);
    formData.append("phone", phone);
    formData.append("address", address);
    if (avatarFile) {
      formData.append("avatar", avatarFile);
    }

    try {
      const response = await fetch(`http://localhost:4000/auth/${decoded.id}`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${token}`,
          Accept: "application/json",
          // Không set 'Content-Type' khi dùng FormData
        },
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        alert("Cập nhật thành công!");
        setAvatarUrl(result.avatar || avatarUrl);
        setAvatarFile(null); // reset file đã upload
      } else {
        const errorData = await response.json();
        alert("Cập nhật thất bại: " + (errorData.message || "Lỗi không xác định"));
      }
    } catch (error) {
      alert("Lỗi khi cập nhật: " + error.message);
    }
  };

  return (
    <>
      <h2 className="text-2xl font-semibold text-gray-700 mb-6">
        Cập nhật thông tin
      </h2>
      <form className="space-y-6" onSubmit={handleSubmit}>
        <div>
          <label className="block mb-1">Tên</label>
          <input
            type="text"
            className="w-full border px-4 py-2 rounded"
            placeholder="Nhập tên"
            value={fullname}
            onChange={(e) => setFullname(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-1">Số điện thoại</label>
          <input
            type="text"
            className="w-full border px-4 py-2 rounded"
            placeholder="Nhập số điện thoại"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-1">Địa chỉ</label>
          <input
            type="text"
            className="w-full border px-4 py-2 rounded"
            placeholder="Nhập địa chỉ"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>
        <div>
          <label className="block mb-1">Ảnh đại diện</label>
          <img
            src={avatarUrl}
            alt="Avatar Preview"
            className="w-24 h-24 object-cover rounded-full mb-2"
          />
          <input
            type="file"
            accept="image/*"
            onChange={handleAvatarChange}
            className="w-full"
          />
        </div>
        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Cập nhật
        </button>
      </form>
    </>
  );
};

export default InfoForm;
