// components/products/ProductsList.js
import React from "react";
import useFetch from "../../hooks/useFetch";
import ProductCard from "./ProductCard";

const ProductsList = () => {
  const { data: products, loading, error } = useFetch("/api/products"); // Thay đổi endpoint nếu cần

  if (loading) return <p className="text-center">Đang tải sản phẩm...</p>;
  if (error) return <p className="text-center text-red-500">Lỗi: {error}</p>;

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 px-4 py-8">
      {products.map((product) => (
        <ProductCard key={product._id} {...product} />
      ))}
    </div>
  );
};

export default ProductsList;
