  role: decoded.role,
  slug: decoded.slug,
  picture: decoded.picture,
});
    swal("Welcome!", "Signed in with Google", "success");
    navigate("/");
  } catch (err) {
    console.error(err);
    swal("Oops!", err.message || "Đã xảy ra lỗi", "error");
  } finally {
    setIsLoading(false);
  }
};


  const signOutUser = async () => {
    setIsLoading(true);
    try {
      await signOut(auth);
      setUser({});
      swal("Logged out", "You have been signed out", "success");
      navigate("/signin");
    } catch (err) {
      swal("Oops!", err.message, "error");
    } finally {
      setIsLoading(false);
    }
  };

  return {
    user,
    isLoading,
    signInWithGoogle,
    signOutUser,
  };
};

export default useFirebase;
