import React, { useState } from 'react';
import OrderCard from './OrderCard';
import useOrder from '../../hooks/useOrder';

const OrderSummary = ({ onPlaceOrder }) => {
  const { cartItems, updateQuantity, removeProduct } = useOrder();

  // Thông tin khách hàng
  const [paymentMethod, setPaymentMethod] = useState('momo');
  const [shippingAddress, setShippingAddress] = useState({
    recipientName: 'Nguyễn Văn A',
    phone: '0912345678',
    address: '123 Trần Hưng Đạo, Quận 1, TP.HCM',
  });
  const [note, setNote] = useState('Giao trong giờ hành chính');

  // Tính tổng tiền từ cart
  const totalPrice = cartItems.reduce((sum, item) => sum + item.product.price * item.quantity, 0);

  const handleAddressChange = (field, value) => {
    setShippingAddress(prev => ({ ...prev, [field]: value }));
  };

  const handlePlaceOrder = () => {
    const orderData = {
      orderType: "delivery",
      items: cartItems.map(item => ({
        product: item.product._id,
        quantity: item.quantity,
      })),
      paymentMethod,
      shippingAddress,
      note,
    };
    onPlaceOrder(orderData);
  };

  return (
    <div className="space-y-6">
      {cartItems.map(({ product, quantity }) => (
        <OrderCard
          key={product._id}
          product={product}
          quantity={quantity}
          totalPrice={product.price * quantity}
          updateQuantity={updateQuantity}
          removeProduct={removeProduct}
        />
      ))}

      <div className="bg-white p-4 rounded shadow-md space-y-3">
        <h3 className="font-semibold text-lg">Thông tin khách hàng</h3>

        <div>
          <label className="block font-medium">Phương thức thanh toán:</label>
          <select
            value={paymentMethod}
            onChange={e => setPaymentMethod(e.target.value)}
            className="border rounded px-2 py-1 w-full"
          >
            <option value="momo">Momo</option>
            <option value="bank_transfer">Chuyển khoản</option>
            <option value="cash_on_delivery">Thanh toán trực tiếp</option>
          </select>
        </div>

        <div>
          <label className="block font-medium">Tên người nhận:</label>
          <input
            type="text"
            value={shippingAddress.recipientName}
            onChange={e => handleAddressChange('recipientName', e.target.value)}
            className="border rounded px-2 py-1 w-full"
          />
        </div>

        <div>
          <label className="block font-medium">Số điện thoại:</label>
          <input
            type="text"
            value={shippingAddress.phone}
            onChange={e => handleAddressChange('phone', e.target.value)}
            className="border rounded px-2 py-1 w-full"
          />
        </div>

        <div>
          <label className="block font-medium">Địa chỉ:</label>
          <input
            type="text"
            value={shippingAddress.address}
            onChange={e => handleAddressChange('address', e.target.value)}
            className="border rounded px-2 py-1 w-full"
          />
        </div>

        <div>
          <label className="block font-medium">Ghi chú:</label>
          <textarea
            value={note}
            onChange={e => setNote(e.target.value)}
            className="border rounded px-2 py-1 w-full"
          />
        </div>

        <div className="text-right font-bold text-xl">
          Tổng tiền: {totalPrice} VNĐ
        </div>

        <button
          onClick={handlePlaceOrder}
          className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded font-semibold"
        >
          Đặt hàng
        </button>
      </div>
    </div>
  );
};

export default OrderSummary;
