import {
  getAuth,
  signInWithPopup,
  signOut,
  GoogleAuthProvider,
  onAuthStateChanged,
} from "firebase/auth";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import swal from "sweetalert";
import initializeAuthentication from "../config/firebase";
import { jwtDecode } from "jwt-decode";

initializeAuthentication();

const useFirebase = () => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const auth = getAuth();
  const navigate = useNavigate();

  useEffect(() => {
    // 1. Kiểm tra nếu có token trong localStorage => decode và set user
    const token = localStorage.getItem("accessToken");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUser({
          id: decoded.id,
          displayName: decoded.name,
          email: decoded.email,
          role: decoded.role,
          picture: decoded.avatar,
        });
      } catch {
        setUser(null);
      }
    }

    // 2. Firebase auth state listener (dành cho Google login)
    const unsubscribe = onAuthStateChanged(auth, (loggedUser) => {
      if (loggedUser) setUser(loggedUser);
      else setUser(null);
      setIsLoading(false);
    });
    return () => unsubscribe();
  }, [auth]);
  const signInWithGoogle = async () => {
    setIsLoading(true);
    const provider = new GoogleAuthProvider();

    try {
      const result = await signInWithPopup(auth, provider);
      const { email, displayName, photoURL } = result.user;

      const googleUser = {
        email: email,
        fullname: displayName,
        avatar: photoURL,
      };
      const response = await fetch("http://localhost:4000/auth/google-login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(googleUser),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || "Đăng ký Google thất bại");
      }

      const jwtToken = data.data.token; // Token JWT server trả về
      localStorage.setItem("accessToken", jwtToken);
      const decoded = jwtDecode(jwtToken);
      console.log(decoded);

      setUser({
        id: decoded.id,
        displayName: decoded.name,
        email: decoded.email,
        role: decoded.role,
        picture: decoded.avatar,
      });
      swal("Welcome!", "Signed in with Google", "success");
      navigate("/");
    } catch (err) {
      console.error(err);
      swal("Oops!", err.message || "Đã xảy ra lỗi", "error");
    } finally {
      setIsLoading(false);
    }
  };

  const signOutUser = async () => {
    setIsLoading(true);
    try {
      await signOut(auth);
      setUser({});
      swal("Logged out", "You have been signed out", "success");
      navigate("/signin");
    } catch (err) {
      swal("Oops!", err.message, "error");
    } finally {
      setIsLoading(false);
    }
  };

  return {
    user,
    isLoading,
    signInWithGoogle,
    signOutUser,
  };
};

export default useFirebase;
