import {
  getAuth,
  signInWithPopup,
  signOut,
  GoogleAuthProvider,
  onAuthStateChanged,
} from "firebase/auth";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import swal from "sweetalert";
import initializeAuthentication from "../config/firebase";

initializeAuthentication();

const useFirebase = () => {
  const [user, setUser] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const auth = getAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (loggedUser) => {
      if (loggedUser) setUser(loggedUser);
      else setUser({});
      setIsLoading(false);
    });
    return () => unsubscribe();
  }, [auth]);

 const signInWithGoogle = async () => {
  setIsLoading(true);
  const provider = new GoogleAuthProvider();

  try {
    const result = await signInWithPopup(auth, provider);
    const { email, displayName, photoURL } = result.user;

    const googleUser = {
      id: result.user.uid,  // hoặc lấy từ backend trả về _id
      email,
      name: displayName,
      picture: photoURL,
    };

    const response = await fetch("http://localhost:4000/auth/google-login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(googleUser),
    });
 

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.message || "Đăng ký Google thất bại");
    }
 const tokenObject = {
        token: data.accessToken,
        id: data.data.user._id,
      };
    const accessToken = data.accessToken;

    // Lưu token vào localStorage
localStorage.setItem("accessToken", JSON.stringify(tokenObject));
    // Cập nhật user nếu muốn (hoặc từ data.data.user)
    setUser(data.data.user);

    swal("Welcome!", "Signed in with Google", "success");
    navigate("/");
  } catch (err) {
    console.error(err);
    swal("Oops!", err.message || "Đã xảy ra lỗi", "error");
  } finally {
    setIsLoading(false);
  }
};


  const signOutUser = async () => {
    setIsLoading(true);
    try {
      await signOut(auth);
      setUser({});
      swal("Logged out", "You have been signed out", "success");
      navigate("/signin");
    } catch (err) {
      swal("Oops!", err.message, "error");
    } finally {
      setIsLoading(false);
    }
  };

  return {
    user,
    isLoading,
    signInWithGoogle,
    signOutUser,
  };
};

export default useFirebase;
