import React from 'react';
import Bounce from 'react-reveal/Bounce';
import Heading from '../components/Heading';
import Product from '../components/products/Product';
import useFetch from '../hooks/useFetch';

const ProductsScreen = () => {
  const [data] = useFetch('product'); // gọi đúng endpoint API
  const baseUrl = process.env.REACT_APP_API_BASE_URL;
    console.log(baseUrl);    
  return (
    <section className="max-w-screen-xl py-24 mx-auto px-6">
      {/* heading  */}
      <Heading title="Sản phẩm" />
      {/* products  */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10 py-2">
        {data.length > 0 ? (
          data.map(product => (
            <Bounce left key={product.id}>
              <Product {...product} />
            </Bounce>
          ))
        ) : (
          <p>Đang tải dữ liệu...</p>
        )}
      </div>
    </section>
  );
};

export default ProductsScreen;
