import {
  getAuth,
  signInWithPopup,
  signOut,
  GoogleAuthProvider,
  onAuthStateChanged,
} from "firebase/auth";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // đổi useHistory thành useNavigate
import swal from "sweetalert";
import initializeAuthentication from "../config/firebase";

initializeAuthentication();

const useFirebase = () => {
  const [user, setUser] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const auth = getAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (loggedUser) => {
      if (loggedUser) setUser(loggedUser);
      else setUser({});
      setIsLoading(false);
    });
    return () => unsubscribe();
  }, [auth]);

  const signInWithGoogle = async () => {
    setIsLoading(true);
    const provider = new GoogleAuthProvider();
    try {
      const res = await signInWithPopup(auth, provider);
      console.log(res);
       localStorage.setItem("accessToken", res.accessToken);
      localStorage.setItem("refreshToken", res.accessToken);
      // swal("Welcome back!", "Signed in successfully", "success");
      setUser(res.user);
      swal("Welcome!", "Signed in with Google", "success");
      navigate("/");
    } catch (err) {
      swal("Oops!", err.message, "error");
    } finally {
      setIsLoading(false);
    }
  };

  const signOutUser = async () => {
    setIsLoading(true);
    try {
      await signOut(auth);
      setUser({});
      swal("Logged out", "You have been signed out", "success");
      navigate("/signin");
    } catch (err) {
      swal("Oops!", err.message, "error");
    } finally {
      setIsLoading(false);
    }
  };

  return {
    user,
    isLoading,
    signInWithGoogle,
    signOutUser,
  };
};

export default useFirebase;
