import React from 'react';
import Bounce from 'react-reveal/Bounce';
import Heading from '../components/Heading';
import Product from '../components/products/Product';
import useFetch from '../hooks/useFetch';

const ProductsScreen = () => {
  const [response] = useFetch('product'); // gọi đúng endpoint API
const products = response?.data ?? [];
    console.log(response);
    
  return (
    <section className="max-w-screen-xl py-24 mx-auto px-6">
      {/* heading  */}
      <Heading title="Sản phẩm" />
      {/* products  */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10 py-2">
        {products.length > 0 ? (
          products.map(product => (
            <Bounce left key={product._id}>
              <Product {...product} />
            </Bounce>
          ))
        ) : (
          <p>Đang tải dữ liệu...</p>
        )}
      </div>
    </section>
  );
};

export default ProductsScreen;
