import useUser from "../../hooks/useUser"; // lấy user từ token
import useFetch from '../../hooks/useFetch';

const InfoForm = () => {

  const user = useUser();

  console.log(user.id);
const  data  = useFetch(`auth/${user.id}`, {
  asArray: false,
  hasDataKey: true
});   


    return (
        <>
            <h2 className="text-2xl font-semibold text-gray-700 mb-6">Cập nhật thông tin</h2>
            <form className="space-y-6" onSubmit={(e) => e.preventDefault()}>
                <div>
                    <label className="block mb-1">Tên</label>
                    <input type="text" className="w-full border px-4 py-2 rounded" placeholder="Nhập tên" />
                </div>
                <div>
                    <label className="block mb-1">Số điện thoại</label>
                    <input type="text" className="w-full border px-4 py-2 rounded" placeholder="Nhập số điện thoại" />
                </div>
                <div>
                    <label className="block mb-1">Địa chỉ</label>
                    <input type="text" className="w-full border px-4 py-2 rounded" placeholder="Nhập địa chỉ" />
                </div>
                <div>
                    <label className="block mb-1">Ảnh đại diện</label>
                    <input type="file" className="w-full" />
                </div>
                <button className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">Cập nhật</button>
            </form>
        </>
    );
};

export default InfoForm;
