import { useState, useEffect } from "react";
import useFetch from "../../hooks/useFetch";
import { jwtDecode } from "jwt-decode";

const InfoForm = () => {
    const token = localStorage.getItem("accessToken");
    const decoded = jwtDecode(token);

    const data = useFetch(`auth/${decoded.id}`, {
        asArray: false,
        hasDataKey: true,
    });

    // Khởi tạo state mặc định rỗng
    const [fullname, setFullname] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");

    // Khi data thay đổi thì set lại giá trị state
    useEffect(() => {
        if (data.data) {
            setFullname(data.data.fullname || "");
            setPhone(data.data.phone || "");
            setAddress(data.data.address || "");
        }
    }, [data.data]);

    return (
        <>
            <h2 className="text-2xl font-semibold text-gray-700 mb-6">Cập nhật thông tin</h2>
            <form className="space-y-6" onSubmit={(e) => e.preventDefault()}>
                <div>
                    <label className="block mb-1">Tên</label>
                    <input
                        type="text"
                        className="w-full border px-4 py-2 rounded"
                        placeholder="Nhập tên"
                        value={fullname}
                        onChange={(e) => setFullname(e.target.value)}
                    />
                </div>
                <div>
                    <label className="block mb-1">Số điện thoại</label>
                    <input
                        type="text"
                        className="w-full border px-4 py-2 rounded"
                        placeholder="Nhập số điện thoại"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                    />
                </div>
                <div>
                    <label className="block mb-1">Địa chỉ</label>
                    <input
                        type="text"
                        className="w-full border px-4 py-2 rounded"
                        placeholder="Nhập địa chỉ"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                    />
                </div>
                <div>
                    <label className="block mb-1">Ảnh đại diện</label>
                    <input type="file" className="w-full" />
                </div>
                <button className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">Cập nhật</button>
            </form>
        </>
    );
};

export default InfoForm;
