import React, { useState } from "react";

const OrderSummary = ({ products, onPlaceOrder }) => {
  const totalPrice = products.reduce(
    (sum, item) => sum + item.product.price * item.quantity,
    0
  );

  const [paymentMethod, setPaymentMethod] = useState("momo");
  const [shippingAddress, setShippingAddress] = useState({
    recipientName: "",
    phone: "",
    address: "",
  });
  const [note, setNote] = useState("");

  const handleAddressChange = (field, value) => {
    setShippingAddress((prev) => ({ ...prev, [field]: value }));
  };

 const handleSubmit = async () => {
  const orderData = {
    orderType: "delivery",
    paymentMethod,
    shippingAddress,
    note,
    // totalPrice không cần gửi lên backend nếu backend tính lại, nếu backend cần thì giữ
    items: products.map(({ product, quantity }) => ({
      product: product._id,
      quantity,
    })),
  };

  try {
    const response = await fetch("http://localhost:4000/orders", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(orderData),
    });

    if (!response.ok) {
      // Có lỗi trả về từ server
      const errorData = await response.json();
      alert(`Đặt hàng thất bại: ${errorData.message || response.statusText}`);
      return;
    }

    const data = await response.json();
    alert("Đặt hàng thành công!");
    // Có thể reset form hoặc chuyển trang sau khi đặt hàng thành công
    // Ví dụ: onPlaceOrder(data);
  } catch (error) {
    alert("Lỗi mạng hoặc server: " + error.message);
  }
};


  return (
    <div className="bg-white p-6 rounded shadow-md max-w-md mx-auto mt-6">
      <h2 className="text-xl font-semibold mb-4">Thông tin đơn hàng</h2>

      <div className="mb-4">
        <label className="block font-medium mb-1">Phương thức thanh toán</label>
        <select
          value={paymentMethod}
          onChange={(e) => setPaymentMethod(e.target.value)}
          className="border rounded px-3 py-2 w-full"
        >
          <option value="cash">Chuyển khoản</option>
          <option value="card">Thanh toán trực tiếp</option>
        </select>
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Tên người nhận</label>
        <input
          type="text"
          value={shippingAddress.recipientName}
          onChange={(e) => handleAddressChange("recipientName", e.target.value)}
          className="border rounded px-3 py-2 w-full"
          placeholder="Nguyễn Văn A"
        />
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Số điện thoại</label>
        <input
          type="text"
          value={shippingAddress.phone}
          onChange={(e) => handleAddressChange("phone", e.target.value)}
          className="border rounded px-3 py-2 w-full"
          placeholder="0912345678"
        />
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Địa chỉ</label>
        <input
          type="text"
          value={shippingAddress.address}
          onChange={(e) => handleAddressChange("address", e.target.value)}
          className="border rounded px-3 py-2 w-full"
          placeholder="123 Trần Hưng Đạo, Quận 1, TP.HCM"
        />
      </div>

      <div className="mb-4">
        <label className="block font-medium mb-1">Ghi chú</label>
        <textarea
          value={note}
          onChange={(e) => setNote(e.target.value)}
          className="border rounded px-3 py-2 w-full"
          placeholder="Giao trong giờ hành chính"
          rows={3}
        />
      </div>

      <div className="text-right font-bold text-xl mb-4">
        Tổng tiền: {totalPrice.toLocaleString()} VNĐ
      </div>

      <button
        onClick={handleSubmit}
        className="w-full bg-green-600 hover:bg-green-700 text-white py-3 rounded font-semibold"
      >
        Đặt hàng
      </button>
    </div>
  );
};

export default OrderSummary;
