import React, { useEffect, useState } from 'react';

const OrdersList = () => {
    const [orders, setOrders] = useState([]);
    const [openOrderId, setOpenOrderId] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:4000/orders/user/68368de52c2fa25c74a83d28');
                if (!response.ok) throw new Error('Không thể lấy dữ liệu đơn hàng');
                const data = await response.json();
                setOrders(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    const toggleCollapse = (id) => {
        setOpenOrderId(openOrderId === id ? null : id);
    };

    if (loading) return <p>Đang tải đơn hàng...</p>;
    if (error) return <p className="text-red-500">Lỗi: {error}</p>;

    return (
        <div className="space-y-4">
            <h2 className="text-2xl font-semibold text-gray-700 mb-4">Đơn hàng của bạn</h2>
            {orders.length === 0 ? (
                <p>Không có đơn hàng nào.</p>
            ) : (
                orders.map((order) => (
                    <div
                        key={order._id}
                        className="border rounded shadow p-4 bg-white"
                    >
                        <div className="flex items-center justify-between">
                            <div>
                                <h3 className="font-bold text-lg text-gray-800">Mã đơn: {order.orderCode}</h3>
                                <p className="text-sm text-gray-500">Tổng tiền: {order.totalAmount.toLocaleString()}đ</p>
                                <p className="text-sm text-gray-500">Trạng thái: {order.status}</p>
                            </div>
                            <button
                                onClick={() => toggleCollapse(order._id)}
                                className="text-blue-500 underline text-sm"
                            >
                                {openOrderId === order._id ? 'Ẩn chi tiết' : 'Xem chi tiết'}
                            </button>
                        </div>

                        {openOrderId === order._id && (
                            <div className="mt-4 border-t pt-4 text-sm text-gray-700 space-y-2">
                                <p><strong>Ngày tạo:</strong> {new Date(order.createdAt).toLocaleString()}</p>
                                <p><strong>Phương thức thanh toán:</strong> {order.paymentMethod}</p>
                                <p><strong>Ghi chú:</strong> {order.note || '(Không có)'}</p>
                                <p><strong>Người nhận:</strong> {order.shippingAddress.recipientName}</p>
                                <p><strong>Địa chỉ:</strong> {order.shippingAddress.address}</p>
                                <p><strong>SĐT:</strong> {order.shippingAddress.phone}</p>
                                <div>
                                    <strong>Sản phẩm:</strong>
                                    <ul className="list-disc ml-5 mt-1">
                                        {order.items.map((item) => (
                                            <li key={item._id}>
                                                {item.product.name} - SL: {item.quantity} - Giá: {item.price.toLocaleString()}đ
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        )}
                    </div>
                ))
            )}
        </div>
    );
};

export default OrdersList;
