import React, { useEffect, useState } from "react";
import initializeAuthentication from "../../config/firebase";
import {
  collection,
  query,
  where,
  addDoc,
  onSnapshot,
  serverTimestamp,
  orderBy,
} from "firebase/firestore";
import { jwtDecode } from "jwt-decode";
import { db } from "../../config/firebase";

const CommentSection = ({ productId }) => {
  const [comments, setComments] = useState([]);
  const [text, setText] = useState("");
  const token = localStorage.getItem("accessToken");
  const user = token ? jwtDecode(token) : null;
  
  // Realtime load comments
useEffect(() => {
  const fetchComments = async () => {
    try {
      const res = await fetch(`http://localhost:4000/comments/${productId}`);
      const data = await res.json();
      setComments(data);
    } catch (err) {
      console.error("Lỗi tải bình luận:", err);
    }
  };

  if (productId) fetchComments();
}, [productId]);


  //   // Gửi comment
 const handleSendComment = async (parentId = null) => {
  if (!text.trim()) return;

  try {
    await fetch("http://localhost:4000/comments", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // nếu bạn dùng JWT
      },
      body: JSON.stringify({
        productId,
        userId: user?.id,
        content: text,
        parentId,
      }),
    });

    setText("");
  } catch (err) {
    console.error("Gửi bình luận thất bại:", err);
  }
};


  // Render đệ quy replies
  const renderComments = (parentId = null) => {
    return comments
      .filter((c) => c.parentId === parentId)
      .map((comment) => (
        <div key={comment.id} className="pl-4 mt-3 border-l border-gray-300">
          <div className="flex items-center space-x-2">
            <img
              src={comment.avatar || "/default-avatar.png"}
              className="w-6 h-6 rounded-full"
              alt=""
            />
            <span className="font-semibold">{comment.name}</span>
            <span className="text-sm text-gray-400">
              {comment.createdAt?.seconds
                ? new Date(comment.createdAt.seconds * 1000).toLocaleString()
                : "Đang tải..."}
            </span>
          </div>
<p className="ml-8 mt-1 text-gray-700">{comment.content}</p>

          {/* Trả lời bình luận */}
          <div className="ml-8 mt-2">
            <button
              className="text-blue-500 text-sm hover:underline"
                onClick={() => handleSendComment(comment.id)}
            >
              Trả lời
            </button>
            {renderComments(comment.id)}
          </div>
        </div>
      ));
  };

  return (
    <div className="mt-8">
      <h2 className="text-lg font-semibold mb-2">Bình luận</h2>

      {/* Viết bình luận mới */}
      <div className="mb-4">
        <textarea
          rows={3}
          className="w-full p-2 border border-gray-300 rounded"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Viết bình luận của bạn..."
        ></textarea>
        <button
          onClick={() => handleSendComment()}
          className="mt-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Gửi bình luận
        </button>
      </div>

      {/* Hiển thị danh sách bình luận */}
      <div>{renderComments()}</div>
    </div>
  );
};

export default CommentSection;
