import React, { useEffect, useState } from 'react';

const OrdersList = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await fetch('http://localhost:4000/orders/user/68368de52c2fa25c74a83d28');
                if (!response.ok) {
                    throw new Error('Có lỗi khi lấy dữ liệu đơn hàng');
                }
                const data = await response.json();
                setOrders(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    if (loading) return <p>Đang tải đơn hàng...</p>;
    if (error) return <p className="text-red-500">Lỗi: {error}</p>;

    return (
        <div>
            <h2 className="text-2xl font-semibold text-gray-700 mb-6">Đơn hàng của bạn</h2>
            {orders.length === 0 ? (
                <p>Không có đơn hàng nào.</p>
            ) : (
                orders.map((order) => (
                    <div key={order._id} className="mb-6 border p-4 rounded shadow-sm">
                        <h3 className="text-lg font-bold">Mã đơn hàng: {order.orderCode}</h3>
                        <p>Ngày tạo: {new Date(order.createdAt).toLocaleString()}</p>
                        <p>Trạng thái: {order.status}</p>
                        <p>Phương thức thanh toán: {order.paymentMethod}</p>
                        <p>Ghi chú: {order.note}</p>
                        <p className="mt-2 font-semibold">Sản phẩm:</p>
                        <ul className="ml-4 list-disc">
                            {order.items.map((item) => (
                                <li key={item._id}>
                                    {item.product.name} - SL: {item.quantity} - Giá: {item.price.toLocaleString()}đ
                                </li>
                            ))}
                        </ul>
                        <p className="mt-2 font-bold text-blue-600">
                            Tổng tiền: {order.totalAmount.toLocaleString()}đ
                        </p>
                        <p className="mt-2">Người nhận: {order.shippingAddress.recipientName}</p>
                        <p>Địa chỉ: {order.shippingAddress.address}</p>
                        <p>Điện thoại: {order.shippingAddress.phone}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default OrdersList;
