import React, { useEffect, useState } from 'react';
import Bounce from 'react-reveal/Bounce';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

import { Autoplay, Navigation, Pagination } from 'swiper/modules';
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper.min.css";
import useFetch from '../../hooks/useFetch';
import Heading from '../Heading';
import Testimonial from './Testimonial';
SwiperCore.use([Navigation, Pagination, Autoplay]);

const Testimonials = () => {
    // const [data] = useFetch('testimonial');
     const [data, setData] = useState([]);
    
      useEffect(() => {
            fetch('/database/testimonial.json')
                .then(res => res.json())
                .then(data => setData(data))
                .catch(err => console.error("Lỗi khi fetch services:", err));
        }, []);
        
    return (
        <section className="max-w-screen-xl mx-auto px-6 pb-24">
            {/* heading  */}
            <Heading title="Testimonials" />
            {/* testimonials  */}
           <Swiper
  modules={[Navigation, Pagination, Autoplay]}  // ✅ THÊM DÒNG NÀY
  className="mySwiper py-12"
  autoplay={{
    delay: 5000,
    disableOnInteraction: false
  }}
  pagination={true}
  grabCursor={true}
  slidesPerView={3}
  speed={400}
  spaceBetween={20}
  breakpoints={{
    500: { slidesPerView: 1 },
    700: { slidesPerView: 2 },
    1024: { slidesPerView: 3 }
  }}
>
  {data.map(service => (
    <SwiperSlide key={service.id}>
      <Bounce left>
        <Service {...service} />
      </Bounce>
    </SwiperSlide>
  ))}
</Swiper>

        </section>
    )
}

export default Testimonials
